import React from "react";
import Link from "./Link";
import { Cards, GithubLogo, LinkedinLogo, TwitterLogo } from "@phosphor-icons/react";

export default function ShareList() {
    const links = [
        {
            title: "LinkedIn",
            link: "https://github.com/wesleybertipaglia",
            icon: GithubLogo,
            bg: "bg-slate-800",
            color: "text-white"
        },
        {
            title: "Twitter",
            link: "https://twitter.com/wesleyberti_",
            icon: TwitterLogo,
            bg: "bg-sky-500",
            color: "text-white"
        },
        {
            title: "Facebook",
            link: "https://www.linkedin.com/in/wesley-bertipaglia/",
            icon: LinkedinLogo,
            bg: "bg-indigo-500",
            color: "text-white"
        },
        {
            title: "Messager",
            link: "https://twitter.com/wesleyberti_",
            icon: TwitterLogo,
            bg: "bg-sky-500",
            color: "text-white"
        }
    ]

    return (
        <section className="w-full">
            <div className="flex flex-col gap-4">
                {
                    links.map((item) => (
                        <Link 

                            titleAlign="text-center"
                            key={item.title}
                            title={item.title} 
                            link={item.link} 
                            icon={item.icon} 
                            iconSize="16"
                            iconBoxStyle={`h-6 ${item.color} ${item.bg}`}
                            boxStyle="p-2 hover:bg-slate-200"
                            titleStyle="text-start"
                        />
                    ))
                }
            </div>
        </section>
    )
}
